package main
import (
	"bufio"
	"fmt"
	"os"
	"strings"
    "syscall"
	"golang.org/x/term"
)
func main() {
	username, password, _ := credentials()
	fmt.Printf("Username: %s, Password: %s\n", username, password)
}
func credentials() (string, string, error) {
    reader := bufio.NewReader(os.Stdin)
    fmt.Print("Enter Username: ")
    username, err := reader.ReadString('\n')
    if err != nil {
        return "", "", err
    }
    fmt.Print("Enter Password: ")
    bytePassword, err := term.ReadPassword(int(syscall.Stdin))
    if err != nil {
        return "", "", err
    }
    password := string(bytePassword)
    return strings.TrimSpace(username), strings.TrimSpace(password), nil
}

package main
import (
	"fmt"
	"time"
)
type Widget struct {
	name string
}
type WrappedWidget struct {
	Widget       // this is the promoted field
    time.Time    // this is another anonymous field that has a runtime name of Time
	price int64  // normal field
}
func main() {
	widget := Widget{"my widget"}
	wrappedWidget := WrappedWidget{widget, time.Now(), 1234}
	fmt.Printf("Widget named %s, created at %s, has price %d\n",
        wrappedWidget.name, // name is passed on to the wrapped Widget since it's
                            // the promoted field
        wrappedWidget.Time, // We access the anonymous time.Time as Time
        wrappedWidget.price)
	fmt.Printf("Widget named %s, created at %s, has price %d\n",
        wrappedWidget.Widget.name, // We can also access the Widget directly
                                   // via Widget
        wrappedWidget.Time,
        wrappedWidget.price)
}

// ByteSeq not allowed, or must convert func argument first
func foobar[T interface { []byte }](t T) { /* ... */ }
// ByteSeq allowed
func bazquux[T interface { ~[]byte }](t T) { /* ... */ }
func main() {
    b := []byte{0x00, 0x01}
    seq := ByteSeq{0x02, 0x03}
    foobar(b)           // ok
    foobar(seq)         // compiler error
    foobar([]byte(seq)) // ok, allows inference
    foobar[[]byte](seq) // ok, explicit instantiation, then can assign seq to argument type []byte
    bazquux(b)          // ok
    bazquux(seq)        // ok
}

