package main
import (
	"fmt"
)
type SetGetter[V any, T any] interface {
	Set(V)
	Get() V
    *T
}
func SetGetterSlice[V any, T any, PT SetGetter[V, T]](values []V) []T {
	out := make([]T, len(values))
	for i, v := range values {
		p := PT(&out[i])
		p.Set(v)
	}
	return out
}
// Count implements SetGetter interface
type Count struct {
	x int
}
func (c *Count) Set(x int) { c.x = x }
func (c *Count) Get() int  { return c.x }
func main() {
	ints := []int{1, 2, 3, 4, 5}
    // instantiate with base type
	sgs := SetGetterSlice[int, Count](ints)
	for _, s := range sgs {
		fmt.Println(s.Get()) // prints 1,2,3,4,5 each in a newline
	}
}

import (
	"fmt"
	"io"
	"os"
)
func MoveFile(sourcePath, destPath string) error {
	inputFile, err := os.Open(sourcePath)
	if err != nil {
		return fmt.Errorf("Couldn't open source file: %v", err)
	}
	defer inputFile.Close()
	outputFile, err := os.Create(destPath)
	if err != nil {
		return fmt.Errorf("Couldn't open dest file: %v", err)
	}
	defer outputFile.Close()
	_, err = io.Copy(outputFile, inputFile)
	if err != nil {
		return fmt.Errorf("Couldn't copy to dest from source: %v", err)
	}
	inputFile.Close() // for Windows, close before trying to remove: https://stackoverflow.com/a/64943554/246801
	err = os.Remove(sourcePath)
	if err != nil {
		return fmt.Errorf("Couldn't remove source file: %v", err)
	}
	return nil
}

// Create a context that is already canceled
ctx, cancel := context.WithCancel(context.Background())
cancel()
// Create the request with it
r, _ := http.NewRequestWithContext(ctx, "GET", "http://example.com", nil)
// Do it, it will immediately fail because the context is canceled.
_, err := http.DefaultClient.Do(r)
log.Println(err) // Get http://example.com: context canceled
// This prints false, because the http client wraps the context.Canceled
// error into another one with extra information.
log.Println(err == context.Canceled)
// This prints true, because errors.Is checks all the errors in the wrap chain,
// and returns true if any of them matches.
log.Println(errors.Is(err, context.Canceled))

package main
import (
	"archive/zip"
	"fmt"
	"io"
	"os"
	"path/filepath"
)
// Zips "./input" into "./output.zip"
func main() {
	file, err := os.Create("output.zip")
	if err != nil {
		panic(err)
	}
	defer file.Close()
	w := zip.NewWriter(file)
	defer w.Close()
	walker := func(path string, info os.FileInfo, err error) error {
		fmt.Printf("Crawling: %#v\n", path)
		if err != nil {
			return err
		}
		if info.IsDir() {
			return nil
		}
		file, err := os.Open(path)
		if err != nil {
			return err
		}
		defer file.Close()
        // Ensure that `path` is not absolute; it should not start with "/".
        // This snippet happens to work because I don't use
        // absolute paths, but ensure your real-world code
        // transforms path into a zip-root relative path.
		f, err := w.Create(path)
		if err != nil {
			return err
		}
		_, err = io.Copy(f, file)
		if err != nil {
			return err
		}
		return nil
	}
	err = filepath.Walk("input", walker)
	if err != nil {
		panic(err)
	}
}

// Create a context with a very short timeout
ctx, cancel := context.WithTimeout(context.Background(), time.Millisecond)
defer cancel()
// Create the request with it
r, _ := http.NewRequest("GET", "http://example.com", nil)
r = r.WithContext(ctx)
// Do it, it will fail because the request will take longer than 1ms
_, err := http.DefaultClient.Do(r)
log.Println(err) // Get http://example.com: context deadline exceeded
// This prints false, because the http client wraps the context.DeadlineExceeded
// error into another one with extra information.
log.Println(err == context.DeadlineExceeded)
// This prints true, because errors.Is checks all the errors in the wrap chain,
// and returns true if any of them matches.
log.Println(errors.Is(err, context.DeadlineExceeded))

package main
import (
	"crypto/tls"
	"fmt"
	"log"
)
func main() {
    conf := &tls.Config{
		InsecureSkipVerify: true,
	}
	conn, err := tls.Dial("tcp", "www.google.com:443", conf)
	if err != nil {
		log.Println("Error in Dial", err)
		return
	}
	defer conn.Close()
    certs := conn.ConnectionState().PeerCertificates
    for _, cert := range certs {
		fmt.Printf("Issuer Name: %s\n", cert.Issuer)
		fmt.Printf("Expiry: %s \n", cert.NotAfter.Format("2006-January-02"))
        fmt.Printf("Common Name: %s \n", cert.Issuer.CommonName)
	}
}

