}
type testRepository struct {
	store      *datastore.MongoDatastore
}
func (r *testRepository) Find(ctx context.Context , filters interface{}) []Document, error{
    cur, err := r.store.GetCollection("some_collection_name").Find(ctx, filters)
	if err != nil {
		return nil, err
	}
	defer cur.Close(ctx)
	var result = make([]models.Document, 0)
	for cur.Next(ctx) {
		var currDoc models.Document
		err := cur.Decode(&currDoc)
		if err != nil {
			//log here
			continue
		}
		result = append(result, currDoc)
	}
	return result, err
}

func ReverseProxy() gin.HandlerFunc {
    target := "localhost:3000"
    return func(c *gin.Context) {
        director := func(req *http.Request) {
            r := c.Request
            req.URL.Scheme = "http"
            req.URL.Host = target
            req.Header["my-header"] = []string{r.Header.Get("my-header")}
            // Golang camelcases headers
            delete(req.Header, "My-Header")
        }
        proxy := &httputil.ReverseProxy{Director: director}
        proxy.ServeHTTP(c.Writer, c.Request)
    }
}

func renderTemplate(templatePath string, vars interface{}, out io.Writer) error {
	t := template.New(filepath.Base(templatePath))
	var funcMap template.FuncMap = map[string]interface{}{}
	// copied from: https://github.com/helm/helm/blob/8648ccf5d35d682dcd5f7a9c2082f0aaf071e817/pkg/engine/engine.go#L147-L154
	funcMap["include"] = func(name string, data interface{}) (string, error) {
		buf := bytes.NewBuffer(nil)
		if err := t.ExecuteTemplate(buf, name, data); err != nil {
			return "", err
		}
		return buf.String(), nil
	}
	t, err := t.Funcs(sprig.TxtFuncMap()).Funcs(funcMap).ParseFiles(templatePath)
	if err != nil {
		return err
	}
	err = t.Execute(out, &vars)
	if err != nil {
		return err
	}
	return nil
}

const FSPATH = "./build/"
func main() {
	fs := http.FileServer(http.Dir(FSPATH))
	http.HandleFunc("/my_api", func(w http.ResponseWriter, _ *http.Request) { w.Write([]byte("API CALL")) })
	http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
		// If the requested file exists then return if; otherwise return index.html (fileserver default page)
		if r.URL.Path != "/" {
			fullPath := FSPATH + strings.TrimPrefix(path.Clean(r.URL.Path), "/")
			_, err := os.Stat(fullPath)
			if err != nil {
				if !os.IsNotExist(err) {
					panic(err)
				}
				// Requested file does not exist so we return the default (resolves to index.html)
				r.URL.Path = "/"
			}
		}
		fs.ServeHTTP(w, r)
	})
	http.ListenAndServe(":8090", nil)
}

func TestPostRequest(t *testing.T) {
	// Create a new instance of Person
	person := Person{
		Name: "Ryan Alex Martin",
		Age:  27,
	}
	// Marshal it into JSON prior to requesting
	personJSON, err := json.Marshal(person)
	// Make request with marshalled JSON as the POST body
	resp, err := http.Post("https://httpbin.org/anything", "application/json",
		bytes.NewBuffer(personJSON))
	if err != nil {
		t.Error("Could not make POST request to httpbin")
	}
	// That's it!
	// But for good measure, let's look at the response body.
	body, err := ioutil.ReadAll(resp.Body)
	var result PersonResponse
	err = json.Unmarshal([]byte(body), &result)
	if err != nil {
		t.Error("Error unmarshaling data from request.")
	}
	if result.NestedPerson.Name != "Ryan Alex Martin" {
		t.Error("Incorrect or nil name field returned from server: ", result.NestedPerson.Name)
	}
	fmt.Println("Response from server:", result.NestedPerson.Name)
	fmt.Println("Response from server:", result.NestedPerson.Age)
}
type Person struct {
	Name string `json:"name"`
	Age  int    `json:"age"`
}
// NestedPerson is the 'json' field of the response, what we originally sent to httpbin
type PersonResponse struct {
	NestedPerson Person `json:"json"` // Nested Person{} in 'json' field
}

