	}
	defer tx.Rollback(ctx)
	tq := q.WithTx(tx)
	// ... (rest of the transaction remains the same)
    // (it's important to use `tq` not `q` to execute transaction queries
	err = tx.Commit(ctx)
	if err != nil {
		return err
	}
	return nil
}

package logger
import "go.uber.org/zap"
type Logger struct {
	zap *zap.Logger
}
func Must(logger *Logger, err error) *Logger {
	if err != nil {
		panic(err)
	}
	return logger
}
func NewLogger(logFile string) (*Logger, error) {
	zap.NewProductionConfig()
	config := zap.NewProductionConfig()
	config.OutputPaths = []string{"stdout", "./logs/" + logFile}
	logger, err := config.Build(zap.AddCaller())
    if err != nil {
		return nil, err
	}
    return &Logger{zap: logger}, err
}
func (l Logger) Debug(msg string, fields ...zap.Field) {
	l.writer().Debug(msg, fields...)
}
func (l Logger) Info(msg string, fields ...zap.Field) {
	l.writer().Info(msg, fields...)
}
// define all the methdos
var noOpLogger = zap.NewNop()
func (l Logger) writer() *zap.Logger {
	if l.zap == nil {
		return noOpLogger
	}
	return l.zap
}

func main() {
    r := gin.Default()
    r.GET("/ping", func(c *gin.Context) {
        signal := make(chan struct{}, 1)
        go longRunningOperation(signal)
        select {
            case <-signal:
                close(signal) // remember to clean up after yourself
                // move on, will print "Processing"

            case <-c.Request.Context().Done():
                // abort
                return
        }
        log.Print("Processing")
        c.JSON(200, gin.H{
            "message": "pong",
        })
    })
    r.Run()
}
func longRunningOperation(signal chan<- struct{}) {
    time.Sleep(10 * time.Second)
    signal <- struct{}{} // signal that this operation has finished
}

