package main

import (
    "encoding/json"
    "log"
    "reflect"
)

type Foo struct {
    Foo string `json:"test" es:"bar"`
    Bar string `json:"live" es:"baz"`
}
type esFoo Foo
var useESTags = false
func (f Foo) MarshalWithESTag() ([]byte, error) {
    useESTags = true
    data, err := json.Marshal(f)
    useESTags = false
    return data,err
}
func (f Foo) MarshalJSON() ([]byte, error) {
    es := esFoo(f)
    _json,err := json.Marshal(es)
    if useESTags {
        if err != nil {
            goto end
        }
        var intf interface{}
        err = json.Unmarshal(_json, &intf)
        if err != nil {
            goto end
        }
        m := intf.(map[string]interface{})
        _m := make(map[string]interface{},len(m))
        t := reflect.TypeOf(f)
        i := 0
        for _,v := range m {
            tag, found := t.Field(i).Tag.Lookup("es")
            if !found {
                continue
            }
            _m[tag] = v
            i++
        }
        _json,err = json.Marshal(_m)
    }
end:
    return _json,err
}

func main()  {
    f := &Foo{"Hello","World"}
    data, _ := json.Marshal(f)
    log.Println(string(data)) // -> {"test":"Hello","live":"World"}
    data, _ = f.MarshalWithESTag()
    log.Println(string(data)) // -> {"bar":"Hello","baz":"World"}
}

func readDynamically(in []byte) {
    registry, err := createProtoRegistry(".", "addressbook.proto")
    if err != nil {
        panic(err)
    }
    desc, err := registry.FindFileByPath("addressbook.proto")
    if err != nil {
        panic(err)
    }
    fd := desc.Messages()
    addressBook := fd.ByName("AddressBook")
    msg := dynamicpb.NewMessage(addressBook)
    err = proto.Unmarshal(in, msg)
    jsonBytes, err := protojson.Marshal(msg)
    if err != nil {
        panic(err)
    }
    fmt.Println(string(jsonBytes))
    if err != nil {
        panic(err)
    }
}
func createProtoRegistry(srcDir string, filename string) (*protoregistry.Files, error) {
    // Create descriptors using the protoc binary.
    // Imported dependencies are included so that the descriptors are self-contained.
    tmpFile := filename + "-tmp.pb"
    cmd := exec.Command("./protoc/protoc",
        "--include_imports",
        "--descriptor_set_out=" + tmpFile,
        "-I"+srcDir,
        path.Join(srcDir, filename))
    cmd.Stdout = os.Stdout
    cmd.Stderr = os.Stderr
    err := cmd.Run()
    if err != nil {
        return nil, err
    }
    defer os.Remove(tmpFile)
    marshalledDescriptorSet, err := ioutil.ReadFile(tmpFile)
    if err != nil {
        return nil, err
    }
    descriptorSet := descriptorpb.FileDescriptorSet{}
    err = proto.Unmarshal(marshalledDescriptorSet, &descriptorSet)
    if err != nil {
        return nil, err
    }
    files, err := protodesc.NewFiles(&descriptorSet)
    if err != nil {
        return nil, err
    }
    return files, nil
}

