	md, ok := metadata.FromIncomingContext(ss.Context())
	if ok {
		md.Append("X-User-Id", "real_user_id")
	}
	newCtx := metadata.NewIncomingContext(ss.Context(), md)
	return handler(srv, serverStreamWrapper{ss, newCtx})
}

