	ctx := ss.Context()
	return &wrappedStream{
		ss,
		ctx,
	}
}
func UserIDInterceptor(srv interface{}, ss grpc.ServerStream, info *grpc.StreamServerInfo, handler grpc.StreamHandler) error {
	md, ok := metadata.FromIncomingContext(ss.Context())
	if ok {
		md.Append("X-User-Id", "real_user_id")
	}
	newCtx := metadata.NewIncomingContext(ss.Context(), md)
	sw := newStreamContextWrapper(ss)
	sw.SetContext(newCtx)
	return handler(srv, sw)
}

func (r *OHLC_RESS) UnmarshalJSON(d []byte) error {
    // first, decode just the object's keys and leave
    // the values as raw, non-decoded JSON
	var obj map[string]json.RawMessage
	if err := json.Unmarshal(d, &obj); err != nil {
		return err
	}
    // next, look up the "last" element's raw, non-decoded value
    // and, if it is present, then decode it into the Last field
	if last, ok := obj["last"]; ok {
		if err := json.Unmarshal(last, &r.Last); err != nil {
			return err
		}
        // remove the element so it's not in
        // the way when decoding the rest below
		delete(obj, "last")
	}
    // finally, decode the rest of the element values
    // in the object and store them in the Pair field
	r.Pair = make(map[string][]Candles, len(obj))
	for key, val := range obj {
		cc := []Candles{}
		if err := json.Unmarshal(val, &cc); err != nil {
			return err
		}
		r.Pair[key] = cc
	}
	return nil
}

type Artists []Artist
func (a *Artists) Scan(value interface{}) error {
	source, ok := value.(string) // input example ðŸ‘‰ðŸ» {"(david,38,url,1)","(david2,2,\"url 2\",2)"}
	if !ok {
		return errors.New("incompatible type")
	}

	var res Artists
	artists := strings.Split(source, "\",\"")
	for _, artist := range artists {
		for _, old := range []string{"\\\"","\"","{", "}","(",")"} {
			artist = strings.ReplaceAll(artist, old, "")
		}
		artistRawData := strings.Split(artist, ",")
		i, err := strconv.Atoi(artistRawData[1])
		if err != nil {
			return fmt.Errorf("parce ArtistRank raw data (%s) in %d iteration error: %v", artist, i, err)
		}
		res = append(res, Artist{
			ArtistName:     artistRawData[0],
			ArtistId:       artistRawData[1],
			ArtistImageURL: artistRawData[2],
			ArtistRank:     i,
		})
	}
	*a = res
	return nil
}

