var termuiFlag = flag.Bool("termui", false, "run a termui example")
func main() {
	flag.Parse()
	var err error
	if *termuiFlag {
		err = runTermui()
	} else {
		err = runServer()
	}
	if err != nil {
		log.Fatal(err)
	}
}
// runTermui runs the termui "Hello World" example.
func runTermui() error {
	if err := ui.Init(); err != nil {
		return err
	}
	defer ui.Close()
	p := ui.NewParagraph("Hello World!")
	p.Width = 25
	p.Height = 5
	ui.Render(p)
	for e := range ui.PollEvents() {
		if e.Type == ui.KeyboardEvent {
			break
		}
	}
	return nil
}
// runServer listens for TCP connections on a random port and connects
// remote clients to a local PTY running the termui example.
func runServer() error {
	ln, err := net.Listen("tcp", "127.0.0.1:0")
	if err != nil {
		return err
	}
	defer ln.Close()
	log.Printf("Listening for requests on %v", ln.Addr())
	for {
		conn, err := ln.Accept()
		if err != nil {
			return err
		}
		log.Printf("Connecting remote client %v to termui", conn.RemoteAddr())
		go connectTermui(conn)
	}
}
// connectTermui connects a client connection to a termui process running in a
// PTY.
func connectTermui(conn net.Conn) {
	defer func() {
		log.Printf("Closing remote client %v", conn.RemoteAddr())
		conn.Close()
	}()
	t, err := pty.StartWithSize(
		exec.Command(os.Args[0], "--termui"),
		&pty.Winsize{Cols: 80, Rows: 24},
	)
	if err != nil {
		log.Printf("Error starting termui: %v", err)
		return
	}
	defer t.Close()
	go io.Copy(t, conn)
	io.Copy(conn, t)
}

func (r *InternalReconciler) Reconcile(ctx context.Context, req ctrl.Request) (ctrl.Result, error) {
	_ = log.FromContext(ctx)
	internal := examplev1.Internal{}
	if err := r.Get(context.TODO(), types.NamespacedName{
		Name:      req.Name,
		Namespace: req.Namespace,
	}, &internal); err != nil {
		return ctrl.Result{}, err
	}
	external := otherv2.External{}
	if err := r.Get(context.TODO(), types.NamespacedName{
        // note how the name is taken from the internal spec
		Name:      internal.Spec.ExternalResource,
		Namespace: req.Namespace,
	}, &internal); err != nil {
		return ctrl.Result{}, err
	}
	// do something with internal and external here
	return ctrl.Result{}, nil
}

func (r *InternalReconciler) triggerReconcileBecauseExternalHasChanged(o client.Object) []reconcile.Request {
	usedByInternals := &examplev1.InternalList{}
	listOps := &client.ListOptions{
		FieldSelector: fields.OneTermEqualSelector(".spec.ExternalResource", o.GetName()),
		Namespace:     o.GetNamespace(),
	}
	err := r.List(context.TODO(), usedByInternals, listOps)
	if err != nil {
		return []reconcile.Request{}
	}
	requests := make([]reconcile.Request, len(usedByInternals.Items))
	for i, item := range usedByInternals.Items {
		requests[i] = reconcile.Request{
			NamespacedName: types.NamespacedName{
				Name:      item.GetName(),
				Namespace: item.GetNamespace(),
			},
		}
	}
	return requests
}

