	// create the role to simulate that it exists
	role := EmployeeRole{PrivateGormModel{ID: 1}, "Test"}
	require.NoError(DB.Create(&role).Error)
	// avoid re-saving emp.Role
	require.NoError(DB.Omit("Role").Create(&emp).Error)
	// if instead the RoleID doesn't exist
	emp.RoleID = 5
	require.Error(DB.Create(&emp).Error)
}

func main() {
	ch := make(chan int)
	quit := make(chan bool)
	var wg sync.WaitGroup
	wg.Add(2)
	go produce(ch, quit, &wg)
	go consume(ch, &wg)
	time.Sleep(50 * time.Microsecond)
	fmt.Println("CLOSE")
	close(quit)
	wg.Wait()
}
func produce(ch chan int, quit chan bool, wg *sync.WaitGroup) {
	defer wg.Done()
	for i := 0; ; i++ {
		select {
		case <-quit:
			close(ch)
			fmt.Println("exit")
			return
		case ch <- i:
			fmt.Println("Producer sends", i)
		}
	}
}
func consume(ch chan int, wg *sync.WaitGroup) {
	defer wg.Done()

	for i := range ch {
		fmt.Println("Consumer receives", i)
		time.Sleep(time.Microsecond)
	}

	fmt.Println("exit consumer")
	return
}

