}
func NewCache() *Cache {
	return &Cache{make(map[int]int), sync.RWMutex{}}
}
func (c *Cache) Set(id, value int) {
	c.mux.Lock()
	c.store[id] = id
	c.mux.Unlock()
}
func (c *Cache) Get(id int) (int, error) {
	c.mux.RLock()
	v, ok := c.store[id]
	c.mux.RUnlock()
	if !ok {
		return -1, errors.New("a value with given key not found")
