import "slices"
type void struct{}
func longestConsecutive(nums []int) int {
    max_seq := 0
    numSet := make(map[int]void, len(nums))
    for _, n := range nums {
        numSet[n] = void{}
    }
    slices.Sort(nums)
    for _, n := range nums {
        if _, prev_exists := numSet[n-1]; !prev_exists {
            for i := 1; ; i++ {
                if _, next_exists := numSet[n+i]; !next_exists {
                    max_seq = max(max_seq, i)
                    break
                }
            }
        }
    }
    return max_seq
}

