package main
import "fmt"
type flipFlop struct {
	A0, A1 bool
	out0, out1 bool
}
func nor(a, b bool) bool { return !(a || b) }
func (ff *flipFlop)Eval() {
    // Evaluate the circuit until it is stable
	for {
		prev0, prev1 := ff.out0, ff.out1
		ff.out0 = nor(ff.A1, ff.out1)
		ff.out1 = nor(ff.A0, ff.out0)
		if ff.out0 == prev0 && ff.out1 == prev1 {
			break // flip flop is stable
		}
	}
}
func main() {
	var ff flipFlop
	fmt.Println(ff)
    // Pulse a0
	ff.A0 = true
	ff.Eval()
	fmt.Println(ff)
	ff.A0 = false
	ff.Eval()
	fmt.Println(ff)
    // Pulse a1
	ff.A1 = true
	ff.Eval()
	fmt.Println(ff)
	ff.A1 = false
	ff.Eval()
	fmt.Println(ff)
}

