func (s *Typemap) UnmarshalXML(d *xml.Decoder, start xml.StartElement) error {
    nameAttr := ""
    typeAttr := ""
    xsiTypeAttr := ""

    for _, attr := range start.Attr {
        if attr.Name.Space == "" && attr.Name.Local == "name" {
            nameAttr = attr.Value
        }
        if attr.Name.Space == "" && attr.Name.Local == "type" {
            typeAttr = attr.Value
        }
        if attr.Name.Space == "http://www.w3.org/2001/XMLSchema-instance" && attr.Name.Local == "type" {
            xsiTypeAttr = attr.Value
        }
    }

    d.Skip()
    *s = Typemap{Name: nameAttr, Type: typeAttr, XsiType: xsiTypeAttr}

    return nil
}

type ErrorInjectingFakeClient struct {
	client.WithWatch
	withSubResourceSimulation bool
	withObjects               []client.Object
	withStatusSubresource     []client.Object
	failError                 error
}
func NewErrorInjectingFakeClient(scheme *runtime.Scheme, withSubResourceSimulation bool, initObjects ...client.Object) *ErrorInjectingFakeClient {
	builder := fake.NewClientBuilder().
		WithScheme(scheme).
		WithObjects(initObjects...).
		WithStatusSubresource(initObjects...)
	return &ErrorInjectingFakeClient{
		withSubResourceSimulation: withSubResourceSimulation,
		WithWatch:                 builder.Build(),
		withObjects:               initObjects,
		withStatusSubresource:     initObjects,
	}
}
