func (group *Group) MemberWithID(ctx context.Context, id string) (*Member, error) {
	ctx, cancel := context.WithCancel(ctx)
	defer cancel()
	var (
		wg sync.WaitGroup
		member    *Member
		foundOnce sync.Once
		firstNontrivialErr error
		errOnce            sync.Once
	)
	for _, client := range group.Clients {
		wg.Add(1)
		client := client // https://golang.org/doc/faq#closures_and_goroutines
		go func() {
			defer wg.Done()
			m, err := client.ClientMemberWithID(ctx, id)
			if m != nil {
				foundOnce.Do(func() {
					member = m
					cancel()
				})
			} else if nf := (*MemberNotFoundError)(nil); !errors.As(err, &nf) {
				errOnce.Do(func() {
					firstNontrivialErr = err
				})
			}
		}()
	}
	wg.Wait()
	if member == nil {
		if firstNontrivialErr != nil {
			return nil, firstNontrivialErr
		}
		return nil, &MemberNotFoundError{ID: id}
	}
	return member, nil
}

func runProcess(process *exec.Cmd) (stdout, stderr string, err error) {
	outPipe, err := process.StdoutPipe()
	if err != nil {
		return "", "", err
	}
	// Call StderrPipe BEFORE Start().
	// Easy way to do it: outside the goroutine.
	errPipe, err := process.StderrPipe()
	if err != nil {
		return "", "", err
	}
	// Start process.
	if err := process.Start(); err != nil {
		return "", "", err
	}
	// Read stderr in goroutine.
	var wg sync.WaitGroup
	var stderrErr error
	wg.Add(1)
	go func() {
		defer wg.Done()
		data, err := ioutil.ReadAll(errPipe)
		if err != nil {
			stderrErr = err
		} else {
			stderr = string(data)
		}
	}()
	// Read stdout in main thread.
	data, stdoutErr := ioutil.ReadAll(outPipe)
	// Wait until we are done reading stderr.
	wg.Wait()
	// Wait for process to finish.
	if err := process.Wait(); err != nil {
		return "", "", err
	}
	// Handle error from reading stdout.
	if stdoutErr != nil {
		return "", "", stderrErr
	}
	// Handle error from reading stderr.
	if stderrErr != nil {
		return "", "", stderrErr
	}
	stdout = string(data)
	return stdout, stderr, nil
}

