)
func main() {
	rand.Seed(time.Now().UnixNano())
	res, err := MockFunc()
	fmt.Printf("res - %v, err - %v\n", res, err)
}
func MockFunc() ([]int, error) {
	args := []int{1, 2, 3, 4, 5}
	resultChan := make(chan int)
	result := make([]int, 0)
	eg, ctx := errgroup.WithContext(context.Background())
	// Read all results into an array
	eg.Go(func() error {
		for {
			select {
			case <-ctx.Done():
				// Always check to see if the context has cancelled,
				// if there is an error errgroup will cancel the
				// context and all goroutines will need to exit
				// before `eg.Wait` returns.
				return context.Canceled
			case val := <-resultChan:
				result = append(result, val)
				if len(result) == len(args) {
					return nil
				}
			}
		}
	})
	for _, a := range args {
		aLit := a // Copy the value so that we don't re-use the memory address
		eg.Go(func() error {
			value, err := RandomError(aLit)
			if err != nil {
				return err
			}
			select {
			case resultChan <- value:
			case <-ctx.Done():
				return context.Canceled
			default:
			}
			return nil
		})
	}
	return result, eg.Wait()
}
func RandomError(arg int) (int, error) {
	time.Sleep(time.Millisecond * 100 * time.Duration(arg))
	errChance := rand.Intn(100)
	if errChance > 40 {
		fmt.Printf("error on arg - %d\n", arg)
		return 0, errors.New("mock err")
	}
	return errChance, nil
}

func MockFunc() ([]int, error) {
	args := []int{1, 2, 3, 4, 5}
	resultChan := make(chan int, len(args))
	result := make([]int, 0)
	eg, ctx := errgroup.WithContext(context.Background())
	for _, a := range args {
		aLit := a // Copy the value so that we don't re-use the memory address
		eg.Go(func() error {
			value, err := RandomError(aLit)
			if err != nil {
				return err
			}
			select {
			case resultChan <- value:
			case <-ctx.Done():
				return context.Canceled
			default:
			}
			return nil
		})
	}
	if err := eg.Wait(); err != nil {
		return nil, err
	}
	close(resultChan)
	for val := range resultChan {
		result = append(result, val)
	}
	return result, nil
}

package antlr4demo
import (
	"strconv"
	"github.com/antlr/antlr4/runtime/Go/antlr"
)
type EvalVisitor struct {
	BaseExpressionVisitor
	Results map[int]float64
}
func (v *EvalVisitor) Visit(tree antlr.ParseTree) float64 {
	switch val := tree.(type) {
	case *ParseContext:
		return v.VisitParse(val)
	case *MultDivExprContext:
		return v.VisitMultDivExpr(val)
	case *NumberExprContext:
		return v.VisitNumberExpr(val)
	case *PlusSubExprContext:
		return v.VisitPlusSubExpr(val)
	case *NestedExprContext:
		return v.VisitNestedExpr(val)
	case *UnaryExprContext:
		return v.VisitUnaryExpr(val)
	default:
		panic("Unknown context")
	}
}
func (v *EvalVisitor) VisitParse(ctx *ParseContext) float64 {
	for index, expr := range ctx.expr_list {
		v.Results[index] = v.Visit(expr)
	}
	return v.Results[len(v.Results)-1]
}
func (v *EvalVisitor) VisitMultDivExpr(ctx *MultDivExprContext) float64 {
	lhs := v.Visit(ctx.lhs)
	rhs := v.Visit(ctx.rhs)
	if ctx.op.GetTokenType() == ExpressionLexerMULT {
		return lhs * rhs
	} else {
		return lhs / rhs
	}
}
func (v *EvalVisitor) VisitPlusSubExpr(ctx *PlusSubExprContext) float64 {
	lhs := v.Visit(ctx.lhs)
	rhs := v.Visit(ctx.rhs)
	if ctx.op.GetTokenType() == ExpressionLexerPLUS {
		return lhs + rhs
	} else {
		return lhs - rhs
	}
}
func (v *EvalVisitor) VisitNumberExpr(ctx *NumberExprContext) float64 {
	val, _ := strconv.ParseFloat(ctx.NUMBER().GetText(), 10)
	return val
}
func (v *EvalVisitor) VisitNestedExpr(ctx *NestedExprContext) float64 {
	return v.Visit(ctx.Expr())
}
func (v *EvalVisitor) VisitUnaryExpr(ctx *UnaryExprContext) float64 {
	return -v.Visit(ctx.Expr())
}

