type Parent[C any] struct {
	child C
}
func (p *Parent[C]) toQueryString() string {
	query := url.Values{}
	values := reflect.ValueOf(p.child)
	types := values.Type()
	for i := 0; i < values.NumField(); i++ {
		value := values.Field(i).Interface().(string)
		if value != "" {
			query.Add(types.Field(i).Tag.Get("url"), value)
		}
	}
	return query.Encode()
}
