func findChild(obj YamlObject, path ...string) *YamlObject {
	if len(path) == 0 {
		return &obj
	}
	key := path[0]
	child, ok := obj[key]
	if !ok {
		return nil
	}
	obj, ok = child.(YamlObject)
	if !ok {
		return nil
	}
	return findChild(obj, path[1:]...)
}

