type ConcurrentLimiter struct {
    max int
    current int
    mut sync.Mutex
}
func NewConcurrentLimiter(limit int) *ConcurrentLimiter {
    return &ConcurrentLimiter {
        max: limit,
        mut: new(sync.Mutex),
    }
}
func (limiter *ConcurrentLimiter) LimitConcurrentRequests(lmt *limiter.Limiter,
    next http.Handler) http.Handler {
    middle := func(w http.ResponseWriter, r *http.Request) {
        limiter.mut.Lock()
        maxHit := limiter.current == limiter.max
        if maxHit {
            limiter.mut.Unlock()
            httpError := // Insert your HTTP error here
            return
        }
        limiter.current += 1
        limiter.mut.Unlock()
        defer func() {
            limiter.mut.Lock()
            limiter.current -= 1
            limiter.mut.Unlock()
        }()
        // There's no rate-limit error, serve the next handler.
		next.ServeHTTP(w, r)
    }
    return tollbooth.LimitHandler(lmt, http.HandlerFunc(middle))
}

