	"syscall"
	"github.com/sirupsen/logrus"
	"golang.org/x/sys/unix"
)
func main() {
	mac := str2ba("00:19:10:08:FE:08") // YOUR BLUETOOTH MAC ADDRESS HERE
	fd, err := unix.Socket(syscall.AF_BLUETOOTH, syscall.SOCK_STREAM, unix.BTPROTO_RFCOMM)
	check(err)
	addr := &unix.SockaddrRFCOMM{Addr: mac, Channel: 1}
	var data = make([]byte, 50)
	logrus.Print("connecting...")
	err = unix.Connect(fd, addr)
	check(err)
	defer unix.Close(fd)
	logrus.Println("done")
	for {
		n, err := unix.Read(fd, data)
		check(err)
		if n > 0 {
			logrus.Infof("Received: %v\n", string(data[:n]))
		}
	}
}
func check(err error) {
	if err != nil {
		logrus.Fatal(err)
	}
}
// str2ba converts MAC address string representation to little-endian byte array
func str2ba(addr string) [6]byte {
	a := strings.Split(addr, ":")
	var b [6]byte
	for i, tmp := range a {
		u, _ := strconv.ParseUint(tmp, 16, 8)
		b[len(b)-1-i] = byte(u)
	}
	return b
}

func f(ctx context.Context, pool *pgxpool.Pool) (err error) {
    tx, err := pool.Begin(ctx)
    if err != nil {
        return err
    }
    defer func() {
        if err != nil {
            tx.Rollback(ctx)
        } else {
            tx.Commit(ctx)
        }
    }()
    _, err := tx.Exec(ctx, "insert into users (email) values ($1)", "c@em.com")
    if err != nil {
        return err
    }
    var id int
    row := tx.QueryRow(ctx, "select id from users where email = $1", "c@em.com")
    if err := row.Scan(&id); err != nil {
        return err
    }
    // NOTE: the above is just an example, if you need the auto
    // generated id of an inserted record, please use the RETURNING
    // clause supported by PostgreSQL.
    return nil
}

func f(ctx context.Context, pool *pgxpool.Pool) (err error) {
    return pool.BeginFunc(ctx, func(tx pgx.Tx) error {
        _, err := tx.Exec(ctx, "insert into users (email) values ($1)", "c@em.com")
        if err != nil {
            return err
        }
        var id int
        row := tx.QueryRow(ctx, "select id from users where email = $1", "c@em.com")
        if err := row.Scan(&id); err != nil {
            return err
        }
        // NOTE: the above is just an example, if you need the auto
        // generated id of an inserted record, please use the RETURNING
        // clause supported by PostgreSQL.
        return nil
    })
}

