func TestGetTransactionId(t *testing.T) {
	db, mock, err := sqlmock.New(sqlmock.QueryMatcherOption(sqlmock.QueryMatcherEqual))
	if err != nil {
		t.Fatalf("err not expected while opening mock db, %v", err)
	}
	t.Run("HappyPath", func(t *testing.T) {
		rows := sqlmock.NewRows([]string{"tid"}).AddRow("1")
		mock.ExpectQuery("SELECT tid FROM trans_cmdline WHERE cmdline LIKE '%install $1%' ORDER BY tid DESC LIMIT 1").
            WithArgs("tcpdump").
            WillReturnRows(rows)
		got, err := GetTransactionId(db, "tcpdump")
		assert.Equal(t, 1, got)
		assert.Nil(t, err)
	})
	t.Run("NoRowsReturned", func(t *testing.T) {
		mock.ExpectQuery("SELECT tid FROM trans_cmdline WHERE cmdline LIKE '%install $1%' ORDER BY tid DESC LIMIT 1").
            WithArgs("tcpdump").
            WillReturnError(sql.ErrNoRows)
		got, err := GetTransactionId(db, "tcpdump")
		assert.Equal(t, 0, got)
		assert.Equal(t, sql.ErrNoRows, err)
	})
}

func middleware(next http.Handler) http.Handler {
	return http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
		custom_data := make(map[string]any)
		r = r.WithContext(context.WithValue(r.Context(), "custom_data", custom_data))
		// Call the handler
		next.ServeHTTP(w, r)
		// Retrieve custom data from the request object after the request is served
		v := r.Context().Value("custom_data")
		fmt.Printf("Custom data(%T): %v\n", v, v)
		// or use the above defined map directly
		fmt.Printf("Custom data(%T): %v\n", custom_data, custom_data)
	})
}
func handler(w http.ResponseWriter, r *http.Request) {
	m, ok := r.Context().Value("custom_data").(map[string]any)
	if ok && m != nil {
		m["value"] = true
	}
}

package main
import (
	"fmt"
	"runtime"
)
func main() {
	num := 5
	// Buffered channel with 5 values.
	ch := make(chan int)
	defer close(ch)
	for i := 0; i < num; i++ {
		go func(val int) {
			fmt.Printf("Added value: %d to the channel\n", val)
			ch <- val
		}(i)
	}
	fmt.Println("NumGoroutine:", runtime.NumGoroutine())
	// Read from our channel infinitely and increment each time a value has been read and operated upon
	counter := 0
	for {
		// Check our counter and if its == num then break the infinite loop
		if counter == num {
			break
		}
		val := <-ch
		counter++
		func(i int) {
			// I'd like to verify a value was read from ch & it was processed before I increment the counter
			sq := i * i
			fmt.Println(sq)
		}(val)
	}
}

