import (
	"fmt"
	"strconv"
	"gopkg.in/yaml.v3"
)
var input = []byte(`
spec:
  provider:
    infrastructureConfig:
      networks:
        cloudNAT:
          natIPNames:
            - name: test
`)
func set(root *yaml.Node, path []string, value yaml.Node) {
	if len(path) == 0 {
		*root = value
		return
	}
	key := path[0]
	rest := path[1:]
	switch root.Kind {
	case yaml.DocumentNode:
		set(root.Content[0], path, value)
	case yaml.MappingNode:
		for i := 0; i < len(root.Content); i += 2 {
			if root.Content[i].Value == key {
				set(root.Content[i+1], rest, value)
				return
			}
		}
	case yaml.SequenceNode:
		index, err := strconv.Atoi(key)
		if err != nil {
			panic(err)
		}
		set(root.Content[index], rest, value)
	}
}
func main() {
	var doc yaml.Node
	if err := yaml.Unmarshal(input, &doc); err != nil {
		panic(err)
	}
	newVal := yaml.Node{
		Kind:  yaml.ScalarNode,
		Value: "test2",
	}
	set(&doc, []string{"spec", "provider", "infrastructureConfig", "networks", "cloudNAT", "natIPNames", "0", "name"}, newVal)
	out, err := yaml.Marshal(doc.Content[0])
	if err != nil {
		panic(err)
	}
	fmt.Println(string(out))
}

// EnsureValidToken is a middleware that will check the validity of our JWT.
func ensureValidToken(next http.Handler) http.Handler {
	issuerURL, err := url.Parse("https://" + os.Getenv("AUTH0_DOMAIN") + "/")
	if err != nil {
		log.Fatalf("Failed to parse the issuer url: %v", err)
	}
	provider := jwks.NewCachingProvider(issuerURL, 5*time.Minute)
	jwtValidator, err := validator.New(
		provider.KeyFunc,
		validator.RS256,
		issuerURL.String(),
		[]string{os.Getenv("AUTH0_AUDIENCE")},
		validator.WithCustomClaims(
			func() validator.CustomClaims {
				return &CustomClaims{}
			},
		),
		validator.WithAllowedClockSkew(time.Minute),
	)
	if err != nil {
		log.Fatalf("Failed to set up the jwt validator")
	}
	errorHandler := func(w http.ResponseWriter, r *http.Request, err error) {
		log.Printf("Encountered error while validating JWT: %v", err)
		w.Header().Set("Content-Type", "application/json")
		w.WriteHeader(http.StatusUnauthorized)
		w.Write([]byte(`{"message":"Failed to validate JWT."}`))
	}
	middleware := jwtmiddleware.New(
		jwtValidator.ValidateToken,
		jwtmiddleware.WithErrorHandler(errorHandler),
	)
	return middleware.CheckJWT(next)
}
var EnsureValidToken = adaptor.HTTPMiddleware(ensureValidToken)
app := fiber.New()
app.Use(EnsureValidToken)
app.Get("/", func(c *fiber.Ctx) error {
		return c.SendString("Hello, World!")
	})
app.Listen(":3000")

