func newApi(address string, wg *sync.WaitGroup) (*Api, error) {
	if len(address) == 0 {
		address = Addr
	}
	rpcs := rpc.NewServer()
	errR := rpcs.RegisterName("V1", &MethodsV1{ Quit : wg })
	sock, errL := net.Listen("tcp", address)
	if errR != nil {
		return nil, errR
	}
	if errL != nil {
		return nil, errL
	}
	go rpcs.Accept(sock)
	return &Api{server: rpcs, quit: wg}, nil
}
func Server(wg *sync.WaitGroup, addr string) {
	_, err := newApi(addr, wg)
	if err != nil {
		return
	}
	if wg != nil {
		wg.Wait()
	} else {
		<- time.After(time.Second * 50)
	}
}
func Client(path string) (RpcResponse, error) {
	addr := Addr
	if a := os.Getenv("ADDR"); len(a) > 0 {
		addr = a
	}
	resp := RpcResponse{}
	client, errC := rpc.Dial("tcp", addr)
	if nil == errC {
		errC = client.Call("V1.Cat", &path, &resp);
		println(len(resp.Output))
	}
	return resp, errC
}
type RpcResponse struct { Output string }
type MethodsV1 struct {
	Quit *sync.WaitGroup
}
func (m1 *MethodsV1) Cat(path *string, response *RpcResponse) error {
	if m1.Quit != nil {
		defer m1.Quit.Done()
	}
	o, err := os.ReadFile(*path)
	response.Output = string(o)
	return err
}

type boo struct {
	Boo1 string `json:"boo1"`
	// "-" will tell encoding/json to ignore this field during (un)marshaling
	Foo foo `json:"-"`
}
func (b boo) MarshalJSON() ([]byte, error) {
	// Declare a new type using boo's definition, this
	// "copies" boo's structure but not its behaviour,
	// i.e. B has same fields as boo, but zero methods,
	// not even MarshalJSON -- this is necessary to avoid
	// infinite recursive calls to MarshalJSON.
	type B boo
	// Declare a new type that *embeds* those structs whose
	// fields you want to be at the same level.
	type T struct {
		B
		foo
	}
	// Create an instance of the new type with its fields
	// set from the source boo instance and marshal it.
	return json.Marshal(T{B: B(b), foo: b.Foo})
}

