func main() {
    auth, err := jwt.New(jwt.Auth{
        SecretKey: []byte("must change here"),
        // Authenticator authenticates a request and return jwt.MapClaims
        // that contains a user information of the request.
        Authenticator: func(c *gin.Context) (jwt.MapClaims, error) {
            var loginForm LoginForm
            if err := c.ShouldBind(&loginForm); err != nil {
                return nil, jwt.ErrorAuthenticationFailed
            }
            u, ok := authenticate(req.Username, req.Password)
            if ok {
                return nil, jwt.ErrorAuthenticationFailed
            }
            return jwt.MapClaims{
                "username": u.Username,
                "role":     u.Role,
            }, nil
        },
        // UserFetcher takes a jwt.MapClaims and return a user object.
        UserFetcher: func(c *gin.Context, claims jwt.MapClaims) (interface{}, error) {
            username, ok := claims["username"].(string)
            if !ok {
                return nil, nil
            }
            return findByUsername(username)
        },
    })
    // some lines
    e.Use(jwt.ErrorHandler)
    // issue authorization token
    e.POST("/login", auth.AuthenticateHandler)
    // refresh token expiration
    e.POST("/auth/refresh_token", auth.RefreshHandler)
    // role management
    e.GET("/operator/hello", Operator(auth), SayHello) // this is only for Operator
    e.GET("/admin/hello", Admin(auth), SayHello) // this is only for Admin
}
func Operator(m jwt.Auth) gin.HandlerFunc {
	return m.VerifyPerm(func(claims jwt.MapClaims) bool {
		return role(claims).IsOperator()
	})
}
func Admin(m jwt.Auth) gin.HandlerFunc {
	return m.VerifyPerm(func(claims jwt.MapClaims) bool {
		return role(claims).IsAdmin()
	})
}

package valuelog
import (
	"context"
	"io"
	"log/slog"
	"strings"
	"sync"
)
type Handler struct {
	h   slog.Handler
	mu  *sync.Mutex
	out io.Writer
}
func NewHandler(o io.Writer, opts *slog.HandlerOptions) *Handler {
	if opts == nil {
		opts = &slog.HandlerOptions{}
	}
	return &Handler{
		out: o,
		h: slog.NewTextHandler(o, &slog.HandlerOptions{
			Level:       opts.Level,
			AddSource:   opts.AddSource,
			ReplaceAttr: nil,
		}),
		mu: &sync.Mutex{},
	}
}
func (h *Handler) Enabled(ctx context.Context, level slog.Level) bool {
	return h.h.Enabled(ctx, level)
}
func (h *Handler) WithAttrs(attrs []slog.Attr) slog.Handler {
	return &Handler{h: h.h.WithAttrs(attrs), out: h.out, mu: h.mu}
}
func (h *Handler) WithGroup(name string) slog.Handler {
	return &Handler{h: h.h.WithGroup(name), out: h.out, mu: h.mu}
}
func (h *Handler) Handle(ctx context.Context, r slog. Record) error {

	formattedTime := r.Time.Format("2006/01/02 15:04:05")
	//add time and message to values
	strs := []string{formattedTime, r.Message}
	if r.NumAttrs() != 0 {
		r.Attrs(func(a slog.Attr) bool {
			strs = append(strs, a.Value.String())
			return true
		})
	}
	result := strings.Join(strs, " ")
	b := []byte(result)
	h.mu.Lock()
	defer h.mu.Unlock()
	_, err := h.out.Write(b)
	return err
}

// The maximum amount of time the consumer expects a message takes to
		// process for the user. If writing to the Messages channel takes longer
		// than this, that partition will stop fetching more messages until it
		// can proceed again.
		// Note that, since the Messages channel is buffered, the actual grace time is
		// (MaxProcessingTime * ChannelBufferSize). Defaults to 100ms.
		// If a message is not written to the Messages channel between two ticks
		// of the expiryTicker then a timeout is detected.
		// Using a ticker instead of a timer to detect timeouts should typically
		// result in many fewer calls to Timer functions which may result in a
		// significant performance improvement if many messages are being sent
		// and timeouts are infrequent.
		// The disadvantage of using a ticker instead of a timer is that
		// timeouts will be less accurate. That is, the effective timeout could
		// be between `MaxProcessingTime` and `2 * MaxProcessingTime`. For
		// example, if `MaxProcessingTime` is 100ms then a delay of 180ms
		// between two messages being sent may not be recognized as a timeout.

// Input table as a single string
tableString := `State    Version Support eRSTP Enhancements Bridge Priority Hello Time Max Age Time Transmit Count Forward Delay Max Hops Cost Style    BPDU Guard Timeout VersionForSNMP
Disabled RSTP            On                 0               2 s        20 s         32             15 s          20       STP (16 bit)  Don't shutdown     RSTP          `
// Split the table into rows based on newline characters
rows := strings.Split(tableString, "\n")
// If the header and the data rows are not the same, fail
if len(rows[0]) != len(rows[1]) {
	fmt.Println("unaligned table")
	return
	// Other way to handle it may be to pad either the header or the data row
}
var i = len(rows[0]) - 2
var indexes []int
for i >= 0 {
	if string(rows[0][i]) == " " && string(rows[0][i+1]) != " " &&
		string(rows[1][i]) == " " && string(rows[1][i+1]) != " " {
		indexes = append(indexes, i)
	}
	i--
}

