package main
import (
	"context"
	"encoding/json"
	"fmt"
	"os"
	"golang.org/x/sync/errgroup"
)
func main() {
	var myFiles = []string{"file1", "file2", "file3", "file4", "file5", "file6"}
	fileChan := make(chan string)
	dataChan := make(chan fileData)
	g, ctx := errgroup.WithContext(context.Background())
	for i := 0; i < 3; i++ {
		worker_num := i
		g.Go(func() error {
			for file := range fileChan {
				if err := getBytesFromFile(file, dataChan); err != nil {
					fmt.Println("worker", worker_num, "failed to process", file, ":", err.Error())
					return err
				} else if err := ctx.Err(); err != nil {
					fmt.Println("worker", worker_num, "context error in worker:", err.Error())
					return err
				}
			}
			fmt.Println("worker", worker_num, "processed all work on channel")
			return nil
		})
	}
	// dispatch files
	g.Go(func() error {
		defer close(fileChan)
		done := ctx.Done()
		for _, file := range myFiles {
			if err := ctx.Err(); err != nil {
				return err
			}
			select {
			case fileChan <- file:
				continue
			case <-done:
				break
			}
		}
		return ctx.Err()
	})
	var err error
	go func() {
		err = g.Wait()
		close(dataChan)
	}()
	var myMap = make(map[string]string)
	for data := range dataChan {
		myMap[data.name] = data.bytes
	}
	if err != nil {
		fmt.Println("errgroup Error:", err.Error())
	}
	enc := json.NewEncoder(os.Stdout)
	enc.SetIndent("", " ")
	if err := enc.Encode(myMap); err != nil {
		panic(err)
	}
}
type fileData struct {
	name,
	bytes string
}
func getBytesFromFile(file string, dataChan chan fileData) error {
	bytes, err := openFileAndGetBytes(file)
	if err == nil {
		dataChan <- fileData{name: file, bytes: bytes}
	}
	return err
}
func openFileAndGetBytes(file string) (string, error) {
	if file == "file2" {
		return "", fmt.Errorf("%s cannot be read", file)
	}
	return fmt.Sprintf("these are some bytes for file %s", file), nil
}

func makeHTTPRequest(ctx context.Context) (string, error) {
	ch := make(chan string)
	go func() {
		time.Sleep(10 * time.Second)
		select {
		case ch <- "abc":
		default:
            // When context deadline exceeded, there is no receiver
            // This case will prevent goroutine blocking forever
			return
		}
	}()
	select {
	case <-ctx.Done():
		return "", ctx.Err()
	case result := <-ch:
		return result, nil
	}
}
func main() {
	ctx, cancel := context.WithTimeout(context.Background(), 5*time.Second)
	defer cancel()
	fmt.Printf("[%v] Begin make request \n", time.Now())
	abc, err := makeHTTPRequest(ctx)
	if err != nil {
		fmt.Printf("[%v] Return error: %v \n", time.Now(), err)
		return
	}
	fmt.Printf("[%v] %s", time.Now(), abc)
}

func main() {
	resp, err := http.Get("IMAGE URL GOES HERE")
	if err != nil {
		panic(err)
	}
	defer resp.Body.Close()
	img, _, err := image.Decode(resp.Body)
	if err != nil {
		panic(err)
	}
	var start time.Time
	var duration time.Duration
	samples := 10
	var sum time.Duration
	fmt.Println("Samples: ", samples)
	sum = time.Duration(0)
	for i := 0; i < samples; i++ {
		start = time.Now()
		usingAt(img)
		duration = time.Since(start)
		sum += duration
	}
	fmt.Println("*** At avg: ", sum/time.Duration(samples))
	sum = time.Duration(0)
	for i := 0; i < samples; i++ {
		start = time.Now()
		usingPix(img)
		duration = time.Since(start)
		sum += duration
	}
	fmt.Println("*** Pix avg: ", sum/time.Duration(samples))
}
func usingAt(img image.Image) {
	bounds := img.Bounds()
	width, height := bounds.Max.X, bounds.Max.Y
	for y := 0; y < height; y++ {
		for x := 0; x < width; x++ {
			r, g, b, _ := img.At(x, y).RGBA()
			_ = uint8(r >> 8)
			_ = uint8(g >> 8)
			_ = uint8(b >> 8)
		}
	}
}
func usingPix(img image.Image, targetColor colorful.Color) {
	bounds := img.Bounds()
	width, height := bounds.Max.X, bounds.Max.Y
	rgba := image.NewRGBA(bounds)
	draw.Draw(rgba, bounds, img, bounds.Min, draw.Src)
	for y := 0; y < height; y++ {
		for x := 0; x < width; x++ {
			index := (y*width + x) * 4
			pix := rgba.Pix[index : index+4]
			_ = pix[0]
			_ = pix[1]
			_ = pix[2]
		}
	}
}

