)
func Handler(ctx context.Context, event *DynamoDBOrSQSEvent) error {
	switch {
	case event.IsDynamoDBEvent():
		return DynamoDBHandler(ctx, &event.DynamoDBEvent)
	case event.IsSQSEvent():
		return SQSHandler(ctx, &event.SQSEvent)
	default:
		return ErrUnexpectedEvent
	}
}
func DynamoDBHandler(ctx context.Context, event *events.DynamoDBEvent) error {
	fmt.Printf("DynamoDBEvent records: %+v\n", event.Records)
	return nil
}
func SQSHandler(ctx context.Context, event *events.SQSEvent) error {
	fmt.Printf("SQSEvent records: %+v\n", event.Records)
	return nil
}
var ErrUnexpectedEvent = errors.New("unexpected event")

type DynamoDBOrSQSEvent struct {
	DynamoDBEvent events.DynamoDBEvent
	SQSEvent      events.SQSEvent
}
func (e *DynamoDBOrSQSEvent) UnmarshalJSON(data []byte) error {
	if err := json.Unmarshal(data, &e.DynamoDBEvent); err != nil {
		return fmt.Errorf("can't unmarshal DynamoDB event: %w", err)
	}
	if err := json.Unmarshal(data, &e.SQSEvent); err != nil {
		return fmt.Errorf("can't unmarshal SQS event: %w", err)
	}
	return nil
}
func (e *DynamoDBOrSQSEvent) IsDynamoDBEvent() bool {
	records := e.DynamoDBEvent.Records
	return len(records) > 0 && records[0].EventID != ""
}
func (e *DynamoDBOrSQSEvent) IsSQSEvent() bool {
	records := e.SQSEvent.Records
	return len(records) > 0 && records[0].MessageId != ""
}

func (r *Rand) Float64() float64 {
	// A clearer, simpler implementation would be:
	//	return float64(r.Int63n(1<<53)) / (1<<53)
	// However, Go 1 shipped with
	//	return float64(r.Int63()) / (1 << 63)
	// and we want to preserve that value stream.
	//
	// There is one bug in the value stream: r.Int63() may be so close
	// to 1<<63 that the division rounds up to 1.0, and we've guaranteed
	// that the result is always less than 1.0.
	//
	// We tried to fix this by mapping 1.0 back to 0.0, but since float64
	// values near 0 are much denser than near 1, mapping 1 to 0 caused
	// a theoretically significant overshoot in the probability of returning 0.
	// Instead of that, if we round up to 1, just try again.
	// Getting 1 only happens 1/2âµÂ³ of the time, so most clients
	// will not observe it anyway.
again:
	f := float64(r.Int63()) / (1 << 63)
	if f == 1 {
		goto again // resample; this branch is taken O(never)
	}
	return f
}

