func Get[T any]() fiber.Handler {
	return func(c *fiber.Ctx) error {
		models := new([]T)
        result := database.DB.Find(models)
        if result.Error != nil {
           return result.Error
        }
        c.SendStatus(http.StatusOK)
        return c.JSON(models)
    }
}
