	return func(c *fiber.Ctx) error {
		model := new(T)
        err := c.BodyParser(model)
        if err != nil {
            return err
        }
        result := database.DB.Create(model)
        if result.Error != nil{
            return result.Error
        }
        return c.SendStatus(http.StatusCreated)
	}
}

