package main
import (
	"net/http"
	"time"
	"github.com/labstack/echo/v4"
	"github.com/prometheus/client_golang/prometheus"
	"github.com/prometheus/client_golang/prometheus/promhttp"
)
var (
	requestCounter = prometheus.NewCounterVec(
		prometheus.CounterOpts{
			Name: "client_request_count",
			Help: "Total number of requests from client",
		},
		[]string{"client", "server", "method", "route", "status"},
	)
	durationHistorgram = prometheus.NewHistogramVec(
		prometheus.HistogramOpts{
			Name:    "client_request_duration_secs",
			Help:    "Duration of requests from Client",
			Buckets: []float64{0.1, 0.5, 1, 2, 5},
		},
		[]string{"client", "server", "method", "route", "status"},
	)
)
func init() {
	prometheus.MustRegister(requestCounter)
	prometheus.MustRegister(durationHistorgram)
}
func main() {
	e := echo.New()
	e.GET("/", func(c echo.Context) error {
		// the request start time
		start := time.Now()
		req, err := http.NewRequest("GET", "http://localhost:8002", nil)
		if err != nil {
			return c.JSON(http.StatusInternalServerError, err.Error())
		}
		resp, err := http.DefaultClient.Do(req)
		if err != nil {
			return c.JSON(http.StatusInternalServerError, err.Error())
		}
		defer resp.Body.Close()
		labels := prometheus.Labels{
			"client": "Server A", // defines the client server
			"server": "Server B", // defines the outbound request server
			"method": "GET",// HTTP method
			"route":  "/", // Request route
			"status": resp.Status,// Response status
		}
		duration := time.Since(start).Seconds()
		// the duration
		durationHistorgram.With(labels).Observe(duration)
		// request api count
		requestCounter.With(labels).Inc()
		return c.String(http.StatusOK, "[Client -> Server] Request send and received")
	})
	e.GET("/metrics", echo.WrapHandler(promhttp.Handler()))
	if err := e.Start(":8001"); err != nil {
		panic(err)
	}
}

package main
import (
	"flag"
	"fmt"
	corev1 "k8s.io/api/core/v1"
	"k8s.io/apimachinery/pkg/util/runtime"
	"k8s.io/client-go/informers"
	"k8s.io/client-go/kubernetes"
	"k8s.io/client-go/tools/cache"
	"k8s.io/client-go/tools/clientcmd"
	"k8s.io/client-go/util/homedir"
	"k8s.io/klog/v2"
	"path/filepath"
	"time"
)
func main() {
	// parse the .kubeconfig file
	var kubeconfig *string
	if home := homedir.HomeDir(); home != "" {
		kubeconfig = flag.String("kubeconfig", filepath.Join(home, ".kube", "config"), "(optional) absolute path to the kubeconfig file")
	} else {
		kubeconfig = flag.String("kubeconfig", "", "absolute path to the kubeconfig file")
	}
	flag.Parse()
	// create config from the kubeconfig
	config, err := clientcmd.BuildConfigFromFlags("", *kubeconfig)
	if err != nil {
		panic(err)
	}
	// create the clientset
	clientSet, err := kubernetes.NewForConfig(config)
	if err != nil {
		panic(err)
	}
	// stop signal for the informer
	stopper := make(chan struct{})
	defer close(stopper)

	// create shared informers for resources in all known API group versions with a reSync period and namespace
	factory := informers.NewSharedInformerFactoryWithOptions(clientSet, 10*time.Second, informers.WithNamespace("demo"))
	podInformer := factory.Core().V1().Pods().Informer()
	defer runtime.HandleCrash()
	// start informer ->
	go factory.Start(stopper)
	// start to sync and call list
	if !cache.WaitForCacheSync(stopper, podInformer.HasSynced) {
		runtime.HandleError(fmt.Errorf("Timed out waiting for caches to sync"))
		return
	}
	podInformer.AddEventHandler(cache.ResourceEventHandlerFuncs{
		AddFunc:    onAdd, // register add eventhandler
		UpdateFunc: onUpdate,
		DeleteFunc: onDelete,
	})
	// block the main go routine from exiting
	<-stopper
}
func onAdd(obj interface{}) {
	pod := obj.(*corev1.Pod)
	klog.Infof("POD CREATED: %s/%s", pod.Namespace, pod.Name)
}
func onUpdate(oldObj interface{}, newObj interface{}) {
	oldPod := oldObj.(*corev1.Pod)
	newPod := newObj.(*corev1.Pod)
	klog.Infof(
		"POD UPDATED. %s/%s %s",
		oldPod.Namespace, oldPod.Name, newPod.Status.Phase,
	)
}
func onDelete(obj interface{}) {
	pod := obj.(*corev1.Pod)
	klog.Infof("POD DELETED: %s/%s", pod.Namespace, pod.Name)
}

