import (
	"bufio"
	"bytes"
	"fmt"
	"io/ioutil"
	"os"
	"strings"
)
func open(filename string) (*os.File, error) {
	return os.Open(filename)
}
func main() {
	fd, err := open("test.sh")
	if err != nil {
		panic(err)
	}
	defer fd.Close()
	outputA, err := doFast(fd)
	if err != nil {
		panic(err)
	}
	fd.Seek(0, 0)
	outputB, err := doSlow(fd)
	if err != nil {
		panic(err)
	}
	fmt.Println(outputA)
	fmt.Println(outputB)
}
func doFast(fd *os.File) (string, error) {
	b, err := ioutil.ReadAll(fd)
	if err != nil {
		return "", err
	}
	var res strings.Builder
	res.Grow(len(b))
	bLines := bytes.Split(b, []byte("\n"))
	for i := range bLines {
		switch {
		case len(bLines[i]) == 0 || bLines[i][0] == '#':
		case bLines[i][len(bLines[i])-1] != ';':
			res.Write(bLines[i])
			res.WriteString("; ")
		default:
			res.Write(bLines[i])
			res.WriteByte(' ')
		}
	}
	return res.String(), nil
}
func doSlow(fd *os.File) (string, error) {
	var a strings.Builder
	scanner := bufio.NewScanner(fd)
	for scanner.Scan() {
		lines := scanner.Text()
		switch {
		case lines == "" || lines[:1] == "#":
			continue
		case lines[len(lines)-1:] != ";":
			a.WriteString(lines + "; ")
		default:
			a.WriteString(lines + " ")
		}
	}
	return a.String(), nil
}

