		log.Println("decoder: unexpected error: %v", reader.Error)
	}
	if h.Magic != magicBytes {
		log.Println("decoder: invalid avro file")
	}
	schema, err := avro.Parse(string(h.Meta[schemaKey]))
	if err != nil {
		log.Println(err)
	}
	log.Println(schema)
