import (
	"bytes"
	"fmt"
	"slices"
	"unicode/utf16"
)
func replaceString(data []byte, newString string, startPos int) ([]byte, error) {
	if startPos >= len(data) || startPos < 0 {
		return nil, fmt.Errorf("start position out of bounds")
	}
	zero := 0
	terminatorPos := -1
	for i, b := range data[startPos:] {
		// Assumes UTF-16 i.e. 2 bytes per character. The terminator must
		// begin on an even-numbered byte.
		if b != 0 {
			zero = 0
		} else if zero > 0 || i%2 == 0 {
			zero += 1
			if zero == 3 {
				terminatorPos = i + 1 - zero
				break
			}
		}
	}
	if terminatorPos == -1 {
		return nil, fmt.Errorf("termination sequence not found")
	}
	ucs2 := utf16.Encode([]rune(newString))
	buf := &bytes.Buffer{}
	for _, char := range ucs2 {
		buf.WriteByte(byte(char & 0xff))
		buf.WriteByte(byte((char >> 8) & 0xff))
	}
	return slices.Replace(data, startPos, startPos+terminatorPos, buf.Bytes()...), nil
}
func main() {
	// [42 97 0 98 0 99 0 0 0 0 42]
	fmt.Println(replaceString([]byte{42, 0, 0, 1, 2, 3, 0, 0, 0, 0, 42}, "abc", 1))
}

