		successful += <-done
	}
	log.Printf("%d urls input, %d could not be parsed. %d/%d valid URLs successful (%.0f%%)",
		inputted, inputted-submitted,
		successful, submitted,
		float64(successful)/float64(submitted)*100.0,
	)
}

