// Write any data as byte array.
func asbyte(s []byte) string {
	var b strings.Builder
	for i, c := range s {
		if i%19 == 0 {
			b.WriteString("\n\t\t")
		}
		b.WriteString(fmt.Sprintf("%#x, ", c))
	}
	return "[]byte{" + b.String() + "}"
}

