)
type Data struct {
	Identifiers map[string]string `json:"identifiers"`
}
const (
	dbPath     = "resolver.json"
	missingKey = "MISSING-KEY"
)
func main() {
	db, err := jsonfile.Load[Data](dbPath)
	if errors.Is(err, fs.ErrNotExist) {
		db, err = jsonfile.New[Data](dbPath)
	}
	if err != nil {
		log.Fatal(err)
	}
	err = readWrite(db)
	if err != nil {
		log.Fatal(err)
	}
}
func readWrite(db *jsonfile.JSONFile[Data]) error {
	var (
		v         string
		keyExists bool
	)
	db.Read(func(d *Data) {
		v, keyExists = d.Identifiers[missingKey]
	})
	if keyExists {
		fmt.Println(v)
		return nil
	}
	v = uuid.NewString()
	return db.Write(func(d *Data) error {
		if d.Identifiers == nil {
			d.Identifiers = make(map[string]string)
		}
		d.Identifiers[missingKey] = v
		return nil
	})
}

func (r request) EncodeAndSign(key *rsa.PrivateKey) (string, string) {
	data, err := json.Marshal(&r)
	if err != nil {
		panic(err)
	}
    // Base 64 encode the data before signing.
	data_b64 := base64.StdEncoding.Strict().EncodeToString(data)
	hashed := sha256.Sum256([]byte(data_b64))
	signature, err := rsa.SignPKCS1v15(nil, key, crypto.SHA256, hashed[:])
	if err != nil {
		panic(err)
	}
	signature_b64 := base64.StdEncoding.Strict().EncodeToString(signature)
    // Return both the base 64 encoded json data and the base 64 encoded
    // signature.
	return data_b64, signature_b64
}

