package httphelp
func Subrouter(router *http.ServeMux, route string) *http.ServeMux {
	sr := http.NewServeMux()
	route = strings.TrimSuffix(route, "/")
	router.Handle(route, removePrefix(sr, route))
	router.Handle(route+"/", removePrefix(sr, route))
	return sr
}
func removePrefix(h http.Handler, prefix string) http.Handler {
	return http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
		path := r.URL.Path
		r.URL.Path = "/" + strings.TrimPrefix(strings.TrimPrefix(path, prefix), "/")
		h.ServeHTTP(w, r)
		r.URL.Path = path
	})
}

type Message struct {
	Data []byte
}
func (key Message) compare(other Message) int {
	return bytes.Compare(key.Data, other.Data) // make the message comparable
}
func BenchmarkConcurrentMap(b *testing.B) {
	m := cmap.NewWithCustomShardingFunction[*Message, bool](
		func(key *Message) uint32 {
			return adler32.Checksum(key.Data) // use adler32 for sharding only
		})
	totalLoads := 0
	b.ResetTimer()
	b.RunParallel(func(pb *testing.PB) {
		var i int
		for pb.Next() {
			i = mr.Int() % 10000000
			absent := m.SetIfAbsent(keys[i], true) // store the message itself as key
			if !absent {
				// wouldnt process message
				totalLoads++
			}
		}
	})
	b.ReportMetric(float64(totalLoads), "loads")
}
func generateRandomBytes(n int) (*Message, error) {
	m := &Message{make([]byte, n)}
	_, err := rand.Read(m.Data)
	return m, err
}
func generateRandomKeys(n int) []*Message {
	var KEY_SIZE = 1000
	keys := make([]*Message, n)
	keys[0], _ = generateRandomBytes(KEY_SIZE)
	b := make([]byte, 1)
	for i := 1; i < n; i++ {
		keys[i] = &Message{make([]byte, KEY_SIZE)}
		copy(keys[i].Data, keys[i-1].Data)
		rand.Read(b)
		keys[i].Data[mr.Int()%KEY_SIZE] = b[0]
	}
	return keys
}
