		{time.Now().AddDate(0, 0, -2), 4, 1, 2, 3},
		{time.Now().AddDate(0, 0, -1), 10, 5, 6, 7},
	}
	insertStmt := `INSERT INTO securities_price_history VALUES ($1, $2)`
	_, err = conn.Exec(context.Background(), insertStmt, "something", history)
	if err != nil {
		log.Fatal(err)
	}
	sphm := &SecuritiesPriceHistoryModel{}
	selectStmt := `SELECT (symbol, history) FROM securities_price_history WHERE symbol=$1`
	err = conn.QueryRow(context.Background(), selectStmt, "something").Scan(sphm)
	if err != nil {
		log.Fatal(err)
	}
	fmt.Printf("after insert: %v\n", sphm)
	newHistory := append(history, DayPriceModel{time.Now(), 6, 3, 4, 5})
	updateStmt := `UPDATE securities_price_history SET history = $1 WHERE symbol = $2`
	_, err = conn.Exec(context.Background(), updateStmt, newHistory, "something")
	if err != nil {
		log.Fatal(err)
	}
	err = conn.QueryRow(context.Background(), selectStmt, "something").Scan(sphm)
	if err != nil {
		log.Fatal(err)
	}
	fmt.Printf("after update: %v\n", sphm)
}

func getClient() (*client.Client, error) {
	return client.NewClientWithOpts(client.FromEnv)
}
func main() {
	ImagePull()
}
func ImagePull() error {
	cli, err := getClient()
	if err != nil {
		panic(err)
	}
	defer cli.Close()
	events, err := cli.ImagePull(
		context.Background(),
		"busybox:1.35",
		types.ImagePullOptions{},
	)
	if err != nil {
		panic(err)
	}
	defer events.Close()
	wg := sync.WaitGroup{}
	wg.Add(1)
	go func(wg *sync.WaitGroup) {
		time.Sleep(1 * time.Second)
		list, err := cli.ImageList(context.Background(), types.ImageListOptions{})
		if err != nil {
			panic(err)
		}
		for _, ims := range list {
			for _, tag := range ims.RepoTags {
				if strings.HasPrefix(tag, "busybox:1.35") {
					wg.Done()
				}
			}
		}
	}(&wg)
	wg.Wait()
	return nil
}

package repo
import "database/sql"
type Patient struct {
	ID               int
	Name             string
	Surname          string
	Age              int
	InsuranceCompany string
}
type PostgresStore struct {
	// rely on the generic DB provided by the "sql" package
	db *sql.DB
}
func (p *PostgresStore) GetPatient(id int) ([]Patient, error) {
	rows, err := p.db.Query("SELECT id, name, age, insurance FROM patients")
	if err != nil {
		return nil, err
	}
	defer rows.Close()
	items := []Patient{}
	for rows.Next() {
		var i Patient
		if err := rows.Scan(
			&i.ID,
			&i.Name,
			&i.Surname,
			&i.Age,
			&i.InsuranceCompany,
		); err != nil {
			return nil, err
		}
		items = append(items, i)
	}
	if err := rows.Close(); err != nil {
		return nil, err
	}
	if err := rows.Err(); err != nil {
		return nil, err
	}
	return items, nil
}

