func (a *ConnectionInfo) Scan(src any) error {
	switch src := src.(type) {
	case nil:
		return nil
	case []byte:
		var res ConnectionInfo
		err := json.Unmarshal(src, &res)
		*a = res
		return err
	default:
		return fmt.Errorf("scan: unable to scan type %T into struct", src)
	}
}
