import (
	"fmt"
	"net"
	"os"
	"syscall"
)
func main() {
	fd, err := syscall.Socket(syscall.AF_PACKET, syscall.SOCK_RAW, int(htons(syscall.ETH_P_ALL)))
	if err != nil {
		fmt.Fprintf(os.Stderr, "syscall socket: %s", err.Error())
		os.Exit(1)
	}
	// Make a 32KB buffer
	buf := make([]byte, 1<<16)
	for {
		len, sockaddr, err := syscall.Recvfrom(fd, buf, 0)
		if err != nil {
			fmt.Fprintf(os.Stderr, "syscall recvfrom: %s", err.Error())
			os.Exit(1)
		}
		if llsa, ok := sockaddr.(*syscall.SockaddrLinklayer); ok {
			inter, err := net.InterfaceByIndex(llsa.Ifindex)
			if err != nil {
				fmt.Fprintf(os.Stderr, "interface from ifindex: %s", err.Error())
				os.Exit(1)
			}
			fmt.Print(inter.Name + ": ")
		}
		fmt.Printf("% X\n", buf[:len])
	}
}
// htons converts a short (uint16) from host-to-network byte order.
func htons(i uint16) uint16 {
	return (i<<8)&0xff00 | i>>8
}

func Base32Test() {
	// 8 char: 5 * 8 bits -> decodes to 5 bytes
	key, err := base32.StdEncoding.DecodeString("Q4H3AIS4")
	fmt.Println(key)
	if err != nil {
		fmt.Println("test 1, ", err)
	} else {
		fmt.Println("test 1 ok", key)
	}
	// 10 char: 5 * 10 bits -> decodes to 5 bytes and remaider (2 bits but the last 10 bits can not be decode)
	key, err = base32.StdEncoding.DecodeString("Q4H3AIS4CI")
	fmt.Println(key)
	if err != nil {
		fmt.Println("test 2, ", err)
	} else {
		fmt.Println("test 2 ok", key)
	}
	// padding
	key, err = base32.StdEncoding.DecodeString("Q4H3AIS4CI======")
	fmt.Println(key)
	if err != nil {
		fmt.Println("test 3, ", err)
	} else {
		fmt.Println("test 3 ok", key)
	}
}

