	if err != nil {
		return err
	}

	b.Config1.Parameters = c.Config1.Parameters
	b.Config1.Epoch = c.Config1.Epoch
	if b.Config1.Dynamic_keys == nil {
		b.Config1.Dynamic_keys = map[string]dynamic{}
	}
