import (
	"fmt"
	"log"
	"net/http"
	"regexp"
)
var rxNestedKey = regexp.MustCompile(`^(?P<key>[A-Za-z0-9_]+)\[(?P<n_key>[A-Za-z0-9_]+)\]$`)
func Handler(w http.ResponseWriter, r *http.Request) {
	err := r.ParseMultipartForm(0)
	if err != nil {
		return
	}
	multipartForm := map[string]interface{}{}
	nestedBuf := map[string]map[string]interface{}{}
	for key, value := range r.MultipartForm.Value {
		// check key has nested key
		if rxNestedKey.MatchString(key) {
			// parse nested key (nK) and key (k)
			k := rxNestedKey.FindStringSubmatch(key)[1]
			nK := rxNestedKey.FindStringSubmatch(key)[2]
			if nestedBuf[k] == nil {
				nestedBuf[k] = map[string]interface{}{}
			}
			nestedBuf[k][nK] = value
		} else {
			multipartForm[key] = value
		}
	}
	// collect all nested data from buff
	for k, v := range nestedBuf {
		multipartForm[k] = v
	}
	fmt.Printf("%+v\n", multipartForm)
}
func main() {
	http.HandleFunc("/", Handler)
	log.Fatal(http.ListenAndServe("localhost:8080", nil))
}

func (w *Watcher) Watch() (<-chan Event, context.CancelFunc) {
	ctx, cancel := context.WithCancel(context.Background())

	// with small buffer for channel to smooth sending/receiving
	ch := make(chan Event, 5)

	go w.fetch(ctx, cancel, ch)
	return ch, cancel
}
func (w *Watcher) fetch(ctx context.Context, cancel context.CancelFunc, ch chan<- Event) {
    // make sure the context is cancelled always if `fetch` stops to avoid goroutine leak.
	defer cancel()
	for {
		select {
		case <-ctx.Done():
    		// now you can close it without problems
			close(ch)
			return
		case ch <- fetchEvent():
		}
	}
}

