func main() {
	// Some sort of job queue for your workers to process. This job queue should be closed by the process
	// that populates it with items. Once the job channel is closed, any for loops ranging over the channel
	// will read items until there are no more items, and then break.
	jobChan := make(chan JobInfo)
	// Populate the job queue here...
	// ...
	close(jobChan)
	// We now have a full queue of jobs that can't accept new jobs because the channel is closed.
	// Number of concurrent workers.
	workerCount := 10
	// Initialize the WaitGroup.
	wg := sync.WaitGroup{}
	wg.Add(workerCount)
	// Create the worker goroutines.
	for i := 0; i < workerCount; i++ {
		go func() {
			// When the jobChan is closed, and no more jobs are available on the queue, the for loop
			// will exit, causing wg.Done() to be called, and the anonymous function to exit.
			for job := range jobChan {
				// Process job.
			}
			wg.Done()
		}()
	}
	// Wait for all workers to call wg.Done()
	wg.Wait()
	// Whatever you want to do after all queue items have been processed goes here.
	// ...
}

package main
import (
    "fmt"
    "net"
    "sync"
    "time"
)
func main() {
    // This will help you to keep track of the goroutines
    var wg sync.WaitGroup
    for i := 1; i <= 9000; i++ {
        // Increment the counter for each goroutine you start.
        wg.Add(1)
        go func(j int) {
            // Make sure the wait group counter is decremented when the goroutine exits
            defer wg.Done()
            address := fmt.Sprintf("127.0.0.1:%d", j)
            conn, err := net.DialTimeout("tcp", address, 2 * time.Second)
            if err != nil {
               return
            }
            conn.Close()
            fmt.Printf("%d open\n", j)
        }(i)
    }
    // Wait for all goroutines to finish before exiting main
    wg.Wait()
}

package main
import (
	"fmt"
	"log"
	"net"
	"sync"
	"time"
)
var minPort = 1
var maxPort = 65535
var timeout = 2 * time.Second
const parallel = 50
func main(){
	fmt.Println("portscan called")
    // Create a buffered channel with a size equal to the number of goroutines
	ctrl := make(chan int, parallel)
    // Keep track of the currently active goroutines
    var wg sync.WaitGroup
	for p := 1; p <= parallel; p++ {
		wg.Add(1)
        // Start a goroutine...
		go func(p int) {
			log.Printf("Starting goroutine %d", p)

            // ...listening to the control channel.
            // For every value this goroutine reads from the
            // channel...
			for i := range ctrl {
				address := fmt.Sprintf("127.0.0.1:%d", i)
                // ...try to conncet to the port.
				conn, err := net.DialTimeout("tcp", address, timeout)
				if err == nil {
					conn.Close()
					log.Printf("[%3d]: %5d open", p, i)
				}
                // TBD: ERROR HANDLING!!!
			}
            // If the channel is closed, this goroutine is done.
			wg.Done()
			log.Printf("[%3d]: Exiting", p)
		}(p)
	}
    // Fill the control channel with values.
    // If the channel is full, the write operation
    // to the channel will block until one of the goroutines
    // reads a value from it.
	for i := minPort; i <= maxPort; i++ {
		ctrl <- i
	}
    // We have sent all values, so the channel can be closed.
    // The goroutines will finish their current connection attempt,
    // notice that the channel is closed and will in turn call wg.Done().
	close(ctrl)
    // When all goroutines have announced that they are done, we can exit.
	wg.Wait()
}

