func removeLargestPrefix(in []string) []string {
   // Store split paths in results
   results:=make([][]string,0,len(in))
   // Pick the first path as the longest prefix
   results=append(results,strings.Split(in[0],"/"))
   longest:=results[0]
   // cmp shortents the known longest prefix
   cmp:=func(a []string)  {
       if len(a)<len(longest) {
         longest=longest[:len(a)]
       }
       for i:=0;i<len(longest);i++ {
          if a[i]!=longest[i] {
             longest=longest[:i]
             return
          }
       }
   }
   // process all paths
   for i:=1;i<len(in);i++ {
       r:=strings.Split(in[i],"/")
       results=append(results,r)
       cmp(r)
   }
   // here, len(longest) is the length of the longest prefix
   out:=make([]string,0,len(results))
   for _,r:=range results {
      out=append(out,strings.Join(r[len(longest):],"/"))
   }
   return out
}

func (l *INodeList) UnmarshalBSONValue(t bsontype.Type, data []byte) error {
	fmt.Println("INodeList.UnmarshalBSONValue")
	var raw bson.Raw // 1. First, try to decode data as bson.Raw
	err := bson.Unmarshal(data, &raw)
	if err != nil {
		fmt.Println(err)
		return err
	}
	// Slice the raw document to an array of valid raw values
	rawNodes, err := raw.Values()
	if err != nil {
		return err
	}
	// 2. Then, try to decode each bson.Raw as concrete Node according to `nodeclass`
	for _, rawNode := range rawNodes {
		// Convert the raw node to a raw document in order to access its "nodeclass" field
		d, ok := rawNode.DocumentOK()
		if !ok {
			return fmt.Errorf("raw node can't be converted to doc")
		}
		class := d.Lookup("nodeclass").StringValue()
        // Decode the node's raw doc to the corresponding struct
		var node INode
		switch class {
		case "SubNode1":
			node = &SubNode1{}
		case "SubNode2":
			node = &SubNode2{}
		//...
		default:
			// ...
		}
		bson.Unmarshal(d, node)
		*l = append(*l, node)
	}
	return nil
}

