type Instruction struct {
	name string
	array *Array
	field *Field
}
type Array struct {
	name, type_ string
}
type Field struct {
	name, type_ string
	optional    bool
}
func (it *Instruction) UnmarshalXML(d *xml.Decoder, start xml.StartElement) error {
	it.name = start.Name.Local
	switch it.name {
	case "array":
		it.array = &Array{}
		for _, attr := range start.Attr {
			value := attr.Value
			switch attr.Name.Local {
			case "name":
				it.array.name = value
			case "type":
				it.array.type_ = value
			}
		}
	case "field":
		it.field = &Field{}
		for _, attr := range start.Attr {
			value := attr.Value
			switch attr.Name.Local {
			case "name":
				it.field.name = value
			case "type":
				it.field.type_ = value
			case "optional":
				vb, _ := strconv.ParseBool(value)
				it.field.optional = vb
			}
		}
	}
	return d.Skip()
}
func (it Instruction) String() (s string) {
	switch it.name {
	case "array":
		s = fmt.Sprintf("{array: {name:%s type:%s}}", it.array.name, it.array.type_)
	case "break":
		s = "{break: {}}"
	case "field":
		s = fmt.Sprintf("{field: {name:%s type:%s optional:%t}}", it.field.name, it.field.type_, it.field.optional)
	}
	return
}

func (p *Packet) UnmarshalXML(d *xml.Decoder, start xml.StartElement) error {
	for _, attr := range start.Attr {
		switch attr.Name.Local {
		case "family":
			p.Family = attr.Value
		case "action":
			p.Action = attr.Value
		}
	}
	for {
		t, err := d.Token()
		if atEOF(err) {
			break
		}
		if ee, ok := t.(xml.EndElement); ok {
			if ee.Name.Local == start.Name.Local {
				break
			}
		}
		se, ok := t.(xml.StartElement)
		if !ok {
			continue
		}
		if se.Name.Local == "comment" {
			var s string
			must(d.DecodeElement(&s, &se))
			p.Comment = s
			continue
		}
		var dst any
		switch se.Name.Local {
		default:
			continue
		case "array":
			dst = new(Array)
		case "break":
			dst = new(Break)
		case "field":
			dst = new(Field)
		}
		must(d.DecodeElement(dst, &se))
		p.Instructions = append(p.Instructions, dst)
	}
	return nil
}

