func check(e error) {
	if e != nil {
		panic(e)
	}
}
func main() {
	f, err := os.Open("example.yaml")
	check(err)
	defer f.Close()
	input, err := ioutil.ReadAll(f)
	check(err)
	var document yaml.Node
	err = yaml.Unmarshal(input, &document)
	check(err)
	// a MappingNode
	exampleNode := document.Content[0]
	// two string ScalarNodes
	var locationSectionKeyNode, numberSectionKeyNode *yaml.Node
	// two MappingNodes
	var locationSectionValNode, numberSectionValNode *yaml.Node
	for i := 0; i < len(exampleNode.Content); i += 2 {
		node := exampleNode.Content[i]
		if node.Kind == yaml.ScalarNode && node.Value == "locationSection" {
			locationSectionKeyNode = exampleNode.Content[i]
			locationSectionValNode = exampleNode.Content[i+1]
		}
		if node.Kind == yaml.ScalarNode && node.Value == "numberSection" {
			numberSectionKeyNode = exampleNode.Content[i]
			numberSectionValNode = exampleNode.Content[i+1]
		}
	}
	// Print locationSection Nodes
	locationSectionKey, err := yaml.Marshal(locationSectionKeyNode)
	check(err)
	fmt.Println(string(locationSectionKey))
	locationSectionVal, err := yaml.Marshal(locationSectionValNode)
	check(err)
	fmt.Println(string(locationSectionVal))
	// Print numberSection Nodes
	numberSectionKey, err := yaml.Marshal(numberSectionKeyNode)
	check(err)
	fmt.Println(string(numberSectionKey))
	numberSectionVal, err := yaml.Marshal(numberSectionValNode)
	check(err)
	fmt.Println(string(numberSectionVal))
}

