import (
	"bytes"
	"fmt"
	"io"
	"os"
	"github.com/apache/arrow/go/v11/arrow"
	"github.com/apache/arrow/go/v11/arrow/csv"
)
func main() {
	// Create a two-column csv file with this content (the second column has 1024 bytes):
	// 0,000000....
	// 1,111111....
	// 2,222222....
	// 3,333333....
	temp := createTempFile()
	schema := arrow.NewSchema(
		[]arrow.Field{
			{Name: "i64", Type: arrow.PrimitiveTypes.Int64},
			{Name: "str", Type: arrow.BinaryTypes.String},
		},
		nil,
	)
	r := csv.NewReader(
		temp, schema,
		csv.WithComma(','),
		csv.WithChunk(3),
	)
	defer r.Release()
	r.Next()
	// To check what's left after the first chunk is read.
	// If the reader stop at the end of the chunk, the content left will be:
	// 3,333333....
	// But in fact, the content left is:
	// 33333333333
	buf, err := io.ReadAll(temp)
	if err != nil {
		panic(err)
	}
	fmt.Printf("%s\n", buf)
}
func createTempFile() *os.File {
	temp, err := os.CreateTemp("", "test*.csv")
	if err != nil {
		panic(err)
	}
	for i := 0; i < 4; i++ {
		fmt.Fprintf(temp, "%d,", i)
		if _, err := temp.Write(bytes.Repeat([]byte{byte('0' + i)}, 1024)); err != nil {
			panic(err)
		}
		if _, err := temp.Write([]byte("\n")); err != nil {
			panic(err)
		}
	}
	if _, err := temp.Seek(0, io.SeekStart); err != nil {
		panic(err)
	}
	return temp
}

func CheckPositive[T Positive](value T) string {
	switch val := any(value).(type) {
	case decimal.Decimal:
		if val.IsNegative() || val.IsZero() {
			return "non positive decimal"
		}
	case int, int8, int16, int32, int64:
		if reflect.ValueOf(val).Int() <= 0 {
			return "non positive signed"
		}
	case uint, uint8, uint16, uint32, uint64:
		if reflect.ValueOf(val).Uint() == 0 {
			return "non positive unsigned"
		}
	case float32, float64:
		f := reflect.ValueOf(val).Float()
		switch {
		case math.IsNaN(f):
			return "NaN float"
		case math.IsInf(f, -1):
			return "negative infinite"
		case math.IsInf(f, 1):
			// do nothing
		default:
			// not a NaN and not an Infinite
			if f <= 0.0 {
				return "negative float"
			}
		}
	default:
		panic(fmt.Sprintf(`%T is not supported type`, val))
	}
	return "positive"
}

