import (
	"fmt"
)
func main() {
	v := [2]int64{15, 16}
	key := [4]int64{14, 15, 16, 17}
	sample(&v, &key)
}
func sample(v *[2]int64, key *[4]int64) {
	const (
		num_rounds       = 32
		delta      int64 = 1640531527
	)
	for i, sum := 0, int64(0); i < num_rounds; i++ {
		temp := int32(v[1])
		v[0] += int64((((temp << 4) ^ (temp >> 5)) + temp) ^ int32(sum+key[int32(sum)&3]))
		sum -= delta
		temp = int32(v[0])
		v[1] += int64((((temp << 4) ^ (temp >> 5)) + temp) ^ int32(sum+key[(int32(sum)>>11)&3]))
	}
	fmt.Println(*v)
	// Output: [6092213800 11162584543]
}

// implement scanner to decode the raw source
// value as retrieved from the database
func (s *DataCategorySlice) Scan(src any) error {
    var data []byte
    switch v := src.(type) {
    case []byte:
        data = v
    case string:
        data = []byte(v)
    case nil:
        return nil
    default:
        return fmt.Errorf("unsupported type: %T", src)
    }
    if len(data) == 0 {
        return nil
    }
    data = data[1:len(data)-1] // remove surrounding braces
    for _, v := range bytes.Split(data, []byte{','}) {
        *s = append(*s, DataCategory(v))
    }
    return nil
}

