		},
	}
	const tpl = `
{{ range . }}
  {{ tag .Tag "h2" .Content }}
{{ end }}
`
	t, err := template.New("webpage").
		Funcs(template.FuncMap{
			"tag": func(tag, fallback, content string) template.HTML {
