func (p *parser) parseNodeTest(n node, axeTyp string) (opnd node) {
	switch p.r.typ {
	case itemName:
		if p.r.canBeFunc && isNodeType(p.r) {
			var prop string
			switch p.r.name {
			case "comment", "text", "processing-instruction", "node":
				prop = p.r.name
			}
			var name string
			p.next()
			p.skipItem(itemLParens)
			if prop == "processing-instruction" && p.r.typ != itemRParens {
				checkItem(p.r, itemString)
				name = p.r.strval
				p.next()
			}
			p.skipItem(itemRParens)
			opnd = newAxisNode(axeTyp, name, "", prop, n)
		} else {
			prefix := p.r.prefix
			name := p.r.name
			p.next()
			if p.r.name == "*" {
				name = ""
			}
			opnd = newAxisNode(axeTyp, name, prefix, "", n)
		}
	case itemStar:
		opnd = newAxisNode(axeTyp, "", "", "", n)
		p.next()
	default:
		panic("expression must evaluate to a node-set")
	}
	return opnd
}

import (
    "fmt"
    "os"
    "strings"
)
type letter struct {
	Letter string
	Ascii  int
	Table  []string
}
func mapFont(font string) ([]letter, error) {
	readFile, err := os.ReadFile(font)
	if err != nil {
		return nil, fmt.Errorf("could not read the content in the file: %v", err)
	}
	slice := strings.Split(string(readFile), "\n")
	letters := make([]letter, 0)
	index := 31
	for _, row := range slice {
		if row == "" {
			index++
			continue
		}
		grid := make([]string, 0)
		for _, char := range row {
			grid = append(grid, string(char))
		}
		letters = append(letters, letter{
			Letter: string(index),
			Ascii:  index,
			Table:  grid,
		})
	}
	return letters, nil
}
func mapInput(input string, letters []letter) ([]letter, error) {
	lines := strings.Split(input, "\\n")
	output := make([]letter, 0)
	for _, line := range lines {
		characters := []rune(line)
		if line != "" {
			for _, ch := range characters {
				if ch >= 32 && ch <= 126 {
					for _, v := range letters {
						if rune(v.Ascii) == ch {
							output = append(output, v)
						}
					}
				} else {
					return nil, fmt.Errorf("input includes non ascii character(s), please use ascii character(s)")
				}
			}
		} else {
			output = append(output, letter{})
		}
	}
	return output, nil
}
func printInput(str string, letter []letter) []string {
	var pixelRows []string
	for row := 0; row < 8; row++ {
		pixelRow := ""
		for letterIndex := 0; letterIndex < len(str); letterIndex++ {

			character, _ := mapInput(string(str[letterIndex]), letter)
			for column := 0; column < len(character[row].Table); column++
				pixelRow = pixelRow + character[row].Table[column]
			}
		}
		pixelRows = append(pixelRows, pixelRow)
	}
	return pixelRows
}

