func SomeFunction(data interface{}) ([]byte, error) {
	someFunctionInner(reflect.ValueOf(data))
	return json.Marshal(data)
}
func someFunctionInner(v reflect.Value) {
	if v.Kind() == reflect.Pointer {
		v = v.Elem()
	}
	for i := 0; i < v.NumField(); i++ {
		vField := v.Field(i)
		switch vField.Kind() {
		case reflect.Slice:
			if vField.IsNil() {
				vField.Set(reflect.MakeSlice(vField.Type(), 0, 0))
			} else {
				for j := 0; j < vField.Len(); j++ {
					vFieldInner := vField.Index(j)
					if vFieldInner.Kind() != reflect.Struct &&
						(vFieldInner.Kind() != reflect.Pointer || vFieldInner.Elem().Kind() != reflect.Struct) {
						continue
					}
					someFunctionInner(vFieldInner.Index(j))
				}
			}
		case reflect.Pointer, reflect.Struct:
			someFunctionInner(vField)
		default:
		}
	}
}

func filterFile(f, pattern string) (*bytes.Buffer, error) {
	rfd, err := os.Open(f)
	if err != nil {
		log.Fatal(err)
	}
	defer func() {
		if err := rfd.Close(); err != nil {
			log.Fatal(err)
		}
	}()
	reader := bufio.NewReader(rfd)
	return filter(reader, []byte(pattern), 1024*1024)
}
// chunkSize must be larger than the longest line
// a reasonable size is probably >= 64K
func filter(reader io.Reader, pattern []byte, chunkSize int) (*bytes.Buffer, error) {
	var bs []byte
	buffer := bytes.NewBuffer(bs)
	chunk := make([]byte, chunkSize)
	var remaining []byte
	for lastChunk := false; !lastChunk; {
		n, err := readChunk(reader, chunk, remaining, chunkSize)
		if err != nil {
			if err == io.EOF {
				lastChunk = true
			} else {
				return nil, err
			}
		}
		remaining = remaining[:0]
		if !lastChunk {
			for i := n - 1; i > 0; i-- {
				if chunk[i] == '\n' {
					remaining = append(remaining, chunk[i+1:n]...)
					n = i + 1
					break
				}
			}
		}
		s := 0
		for s < n {
			hit := bytes.Index(chunk[s:n], pattern)
			if hit < 0 {
				break
			}
			hit += s
			startOfLine := hit
			for ; startOfLine > 0; startOfLine-- {
				if chunk[startOfLine] == '\n' {
					startOfLine++
					break
				}
			}
			endOfLine := hit + len(pattern)
			for ; endOfLine < n; endOfLine++ {
				if chunk[endOfLine] == '\n' {
					break
				}
			}
			endOfLine++
			_, err = buffer.Write(chunk[s:startOfLine])
			if err != nil {
				return nil, err
			}
			s = endOfLine
		}
		if s < n {
			_, err = buffer.Write(chunk[s:n])
			if err != nil {
				return nil, err
			}
		}
	}
	return buffer, nil
}
func readChunk(reader io.Reader, chunk, remaining []byte, chunkSize int) (int, error) {
	copy(chunk, remaining)
	r := len(remaining)
	for r < chunkSize {
		n, err := reader.Read(chunk[r:])
		r += n
		if err != nil {
			return r, err
		}
	}
	return r, nil
}

