import (
	"fmt"
	"net/http"
	"strings"
	"github.com/gorilla/mux"
)
func main() {
	port := 8008
	r := mux.NewRouter()
	path := "/users"
	readMethods := []string{http.MethodGet}
	updateMethods := []string{http.MethodPut}
	allMethods := []string{http.MethodGet, http.MethodPut}
	r.HandleFunc(path, sendPreflightHeaders(readMethods, readHandler)).Methods(readMethods...)
	r.HandleFunc(path, sendPreflightHeaders(updateMethods, updateHandler)).Methods(updateMethods...)
	r.HandleFunc(path, sendPreflightHeaders(allMethods, optionsHandler)).Methods(http.MethodOptions)
	http.ListenAndServe(fmt.Sprintf(":%d", port), r)
}
func optionsHandler(w http.ResponseWriter, r *http.Request) {
	fmt.Println("options")
}
func readHandler(w http.ResponseWriter, r *http.Request) {
	fmt.Println("read")
}
func updateHandler(w http.ResponseWriter, r *http.Request) {
	fmt.Println("update")
}
func sendPreflightHeaders(allowedMethods []string, next http.HandlerFunc) http.HandlerFunc {
	return http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
		w.Header().Set("Access-Control-Allow-Origin", "http://localhost:3000")
		methodIsValid := false
		for _, method := range allowedMethods {
			if r.Method == method {
				methodIsValid = true
			}
		}
		if !methodIsValid {
			allowedMethodsList := strings.Join(allowedMethods, " or")
			w.Header().Set("Access-Control-Allow-Methods", allowedMethodsList)
			return
		}
		w.Header().Set("Access-Control-Allow-Methods", strings.Join(allowedMethods, ", "))
		w.Header().Set("Access-Control-Allow-Headers", "Authorization, Content-Type, Access-Control-Allow-Credentials, Access-Control-Allow-Origin")
		if r.Method == http.MethodOptions {
			return // is a preflight request
		}
		next.ServeHTTP(w, r)
	})
}

func DeploymentCheck(namespace string, clientset *kubernetes.Clientset) ([]string, []string, []int64, []int64) {
	var deploymentNames []string
	var lastAppliedConfigs []string
	var generations []int64
	var observedGenerations []int64
	deployments, err := clientset.AppsV1().Deployments(namespace).List(context.TODO(), metav1.ListOptions{})
	if errors.IsNotFound(err) {
		log.Print("No deployments in the namespace", err)
	} else if err != nil {
		log.Print("Failed to fetch deployments in the namespace", err)
	}
	for _, deployment := range deployments.Items {
		deploymentNames = append(deploymentNames, deployment.Name)
		lastAppliedConfig := deployment.GetAnnotations()["kubectl.kubernetes.io/last-applied-configuration"]
		lastAppliedConfigs = append(lastAppliedConfigs, lastAppliedConfig)
		generations = append(generations, deployment.Generation)
		observedGenerations = append(observedGenerations, deployment.Status.ObservedGeneration)
	}
	return deploymentNames, lastAppliedConfigs, generations, observedGenerations
}

